import React, { useState } from 'react';
import './styles/FAQstyle.css';

const FAQComp = () => {
  const [selected, setSelected] = useState(null);
  const [answer, setAnswer] = useState(null);

  const questions = [
    { q: 'What is your return policy?', a: 'You can return any item within 30 days of purchase.' },
    { q: 'How do I track my order?', a: 'You will receive a tracking number via email once your order has been shipped.' },
    { q: 'Can I change my order?', a: 'Please contact customer service to change your order.' },
    { q: 'What is your return policy?', a: 'You can return any item within 30 days of purchase.' },
    { q: 'How do I track my order?', a: 'You will receive a tracking number via email once your order has been shipped.' },
    { q: 'Can I change my order?', a: 'Please contact customer service to change your order.' }
  ];
  return (
    <div id="faqSection">
      <h1>Frequently Asked Questions</h1>
      <div className="faq-container">
        {questions.map((item, index) => (
          <div className="faqItem" id={answer === index ? "selectedQ" : "null"} onClick={answer === index ? () => setAnswer(null) : () => setAnswer(index)}>
            <div className="questionTop">
              <svg id="questionIcon" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="w-6 h-6">
                <path stroke-linecap="round" stroke-linejoin="round" d="M12 4.5v15m7.5-7.5h-15" />
              </svg>
              <div className="question">
                {item.q}
              </div>
            </div>
            <div className="answer">
              {item.a}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FAQComp;
